import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Register() {
    const navigate = useNavigate();
    const [user, setUser] = useState([
        { key: 'user_name', value: "" },
        { key: 'email_address', value: "" },
        { key: 'phone_number', value: "" },
        { key: 'password', value: "" },
    ]);
    const [message, setMessage] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser((prevState) =>
            prevState.map((field) =>
                field.key === name ? { ...field, value } : field
            )
        );
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Convert the array back to an object before sending to the server
        const userObj = user.reduce((acc, curr) => {
            acc[curr.key] = curr.value;
            return acc;
        }, {});
        const content = {
            subject: "Email Verification",
            message: "Please use the code below to verify your email.",
        };
        // Send the userObj as the data to the API
        const response = await axios.post("http://127.0.0.1:8000/register", {
            ...userObj,
            content,
        });
    };

    return (
        <div>
            <h2>Register</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="user_name"
                    placeholder="Name"
                    value={user.user_name}
                    onChange={handleChange}
                    required
                />
                <input
                    type="email"
                    name="email_address"
                    placeholder="Email"
                    value={user.email_address}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="phone_number"
                    placeholder="Phone Number"
                    value={user.phone_number}
                    onChange={handleChange}
                    required
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={user.password}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Register</button>
            </form>

            {message && <p>{message}</p>}

        </div>
    );
}

export default Register;
