import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";
import axios from "axios";

function VerifyEmail() {
    const location = useLocation();
    const email = location.state?.email; // Email passed from the registration page

    const [emailValidate, setEmailValidate] = useState({
        email: email || '',  // Set email from location.state if available
        code: '',
    });
    const [message, setMessage] = useState('');
    // Update the state when the input changes
    const handleChange = (e) => {
        setEmailValidate({
            ...emailValidate,
            [e.target.name]: e.target.value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            await axios.post("http://127.0.0.1:8000/verify-email/", emailValidate);
            setMessage("Email verified successfully!");
        } catch (error) {
            setMessage(error.response.data.detail || "Verification failed.");
        }
    };

    return (
        <div>
            <h2>Verify Email</h2>
            <p>A verification code has been sent to: {emailValidate.email}</p>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="code" // Added name="code" for correct binding
                    placeholder="Verification Code"
                    value={emailValidate.code}
                    onChange={handleChange} // Handle input change
                    required
                />
                <button type="submit">Verify</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}

export default VerifyEmail;
