import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

function Register() {
    const navigate = useNavigate();

    // Use an object to store form data instead of an array
    const [user, setUser] = useState({
        user_name: "",
        email_address: "",
        phone_number: "",
        password: "",
    });

    const [message, setMessage] = useState("");

    const handleChange = (e) => {
        const { name, value } = e.target;
        setUser((prevState) => ({
            ...prevState,
            [name]: value, // Update the correct field dynamically
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Basic validation
        if (!/^\w{3,50}$/.test(user.user_name)) {
            setMessage("Invalid username.");
            return;
        }
        if (!/^\d{10}$/.test(user.phone_number)) {
            setMessage("Phone number must be 10 digits.");
            return;
        }
        if (user.password.length < 8) {
            setMessage("Password must be at least 8 characters.");
            return;
        }

        const content = {
            subject: "Email Verification",
            message: "Please use the code below to verify your email.",
        };

        try {
            const response = await axios.post("http://127.0.0.1:8000/register", {
                user: user, // Send the form data as the "user" object
                content,
            });
            setMessage("Registration successful! Check your email.");
        } catch (error) {
            console.error(error);
            console.log(response)
            setMessage(error.response?.data?.detail || "An error occurred.");
        }
    };

    return (
        <div>
            <h2>Register</h2>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    name="user_name"
                    placeholder="Name"
                    value={user.user_name}
                    onChange={handleChange}
                    required
                />
                <input
                    type="email"
                    name="email_address"
                    placeholder="Email"
                    value={user.email_address}
                    onChange={handleChange}
                    required
                />
                <input
                    type="text"
                    name="phone_number"
                    placeholder="Phone Number"
                    value={user.phone_number}
                    onChange={handleChange}
                    required
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    value={user.password}
                    onChange={handleChange}
                    required
                />
                <button type="submit">Register</button>
            </form>

            {message && <p>{message}</p>}
        </div>
    );
}

export default Register;
